(function() {
    'use strict';

    angular.module('chatApp', ['ui.router','ui.bootstrap', 'firebase', 'ngStorage', 'ngEmoji', 'ngSanitize', 'ngScrollGlue'])
        .config(['$urlRouterProvider', '$stateProvider', function($urlRouterProvider, $stateProvider) {


            $urlRouterProvider.otherwise('/home');

            $stateProvider
                .state('home', {
                    url: '/home',
                    templateUrl: 'components/home/home.html',
                    controller: 'HomeController',
                    onEnter: ['loginService', '$state', function (loginService, $state) {
                        loginService.isLoggedIn().then(function (user) {
                            console.log(user);
                            if (user) {
                                $state.go('chat');
                            }
                        });
                    }]
                })
            .state('chat', {
                url: '/chat',
                resolve: {
                    loggedUser: ['loginService', '$state', function (loginService, $state) {
                        return loginService.isLoggedIn().then(function (user) {
                            if (user) {
                                return user;
                            }

                            $state.go('home');
                        });
                    }]
                },
                params:{
                    userKey: null
                },
                templateUrl: 'components/chat/chat.html',
                controller: 'ChatController'
            })

            .state('chat.room', {
                url: '/:roomName',
                params:{
                    userKey: null
                },
                templateUrl: 'components/chat-room/chat.room.html',
                controller: 'ChatRoomController'
            })
        }])

    .run(['$emoji', '$rootScope', function($emoji,$rootScope) {
        //$rootScope.config.hideRooms = false;
        $rootScope.config = {
            hideRooms: false
        };

        $emoji.setConfig({
            img_dir: 'http://hassankhan.github.io/emojify.js/images/emoji'
        })
    }]);

})();
